con:testCase.con:setupScript:
import nl.coin.Selenium.*;
def funcHandle = new ReadymadeFunctions();
import groovy.time.TimeCategory
import java.util.Random
def today = new Date() 
def now = String.format('%tFT%<tT', new Date() )
def wishdate = today 
use(TimeCategory) {
	   wishdate = wishdate + 1.day
}
def wishdatestr = wishdate.format("yyyy-MM-dd'T'HH:mm:ss")
testRunner.testCase.setPropertyValue("Now",now)
testRunner.testCase.setPropertyValue("wishDate",wishdatestr)
String contractId = funcHandle.getRandomString()
testRunner.testCase.setPropertyValue("ContractId",contractId)
String iban = funcHandle.getRandomString("0123456789", 3, "");
testRunner.testCase.setPropertyValue("iban",iban)
String telnr = "0" + funcHandle.getRandomString("0123456789", 9, "");
testRunner.testCase.setPropertyValue("telnr",telnr)
String startnr = ( ('1'..'9')).join()
String letters = ( ('A'..'Z')).join()
Random rand = new Random()  
int max = 4
int housenrlength =  rand.nextInt(max) +1
String postcode = RandomStringUtils.random(1, startnr.toCharArray()) + RandomStringUtils.random(3, nrs.toCharArray()) + RandomStringUtils.random(2, letters.toCharArray())
String housenr = RandomStringUtils.random(housenrlength, startnr.toCharArray())
testRunner.testCase.setPropertyValue("postcode",postcode)
testRunner.testCase.setPropertyValue("housenr",housenr)

con:config.script:
def soapversion = testRunner.testCase.testSuite.getPropertyValue( "soapVersion" )
def dossiertype = testRunner.testCase.testSuite.getPropertyValue( "dossierType" )
def donorsoapversion = testRunner.testCase.testSuite.getPropertyValue( "donorSoapVersion" )
if (soapversion == 'v1' || dossiertype == '<business>Y</business>'|| donorsoapversion =='v1'){
   testRunner.gotoStepByName( "Delay0")
    }
else {
   testRunner.gotoStepByName( "CTR-Sunny Day")	
}

