con:testCase.con:setupScript:
import nl.coin.Selenium.*;
def funcHandle = new ReadymadeFunctions();
;
import groovy.time.TimeCategory
def today = new Date() 
def now = String.format('%tFT%<tT', new Date() )
def wishdate = today 
use(TimeCategory) {
	  wishdate = today + 1.day
}
def wishdatestr = wishdate.format("yyyy-MM-dd'T'HH:mm:ss")
testRunner.testCase.setPropertyValue("Now",now)
testRunner.testCase.setPropertyValue("wishDate",wishdatestr)
String contractId = funcHandle.getRandomString()
testRunner.testCase.setPropertyValue("contractId",contractId)
import groovy.sql.Sql;
def  con = funcHandle.createDBConObject(testCase);
def query = con.rows("select ebc_comp_only  from overstappen.error_blocking_code where ebc_code='8' and ebc_type = 'B'" )
def tmp = query.join(", ")
def componly = tmp.substring(tmp.lastIndexOf("ebc_comp_only:") + 14, tmp.length()-1)
if (componly == "false") {
    query = con.executeUpdate("update overstappen.error_blocking_code set ebc_comp_only = true where ebc_code='8' and ebc_type = 'B'")
    log.info("Blocking code 8 set to COMP only")
}

con:config.script:
def dossiertype = testRunner.testCase.testSuite.getPropertyValue( "dossierType" )
if (dossiertype == '<business>Y</business>'){
   testRunner.gotoStepByName( "Delay0")
    }
else {
   testRunner.gotoStepByName( "CTR-Sunny Day")	
}

