con:testCase.con:setupScript:
import nl.coin.Selenium.*;
def funcHandle = new ReadymadeFunctions();
import groovy.time.TimeCategory
def maxdays = testRunner.testCase.testSuite.getPropertyValue("MaxDays")
def maxbusdays = testRunner.testCase.testSuite.getPropertyValue("MaxBusinessDays")
def business = testRunner.testCase.testSuite.getPropertyValue("dossierType")
int plusdays = 0
if (business == '<business>Y</business>'){
     plusdays = maxbusdays as int
}
else {
     plusdays = maxdays as int
}
plusdays = plusdays + 1
def today = new Date() 
def now = today.format("yyyy-MM-dd'T'HH:mm:ss")
def toolatedate = null
use(TimeCategory) {
	   toolatedate = today + plusdays.days
}
def toolatedatestr = toolatedate.format("yyyy-MM-dd'T'HH:mm:ss")
def toolatedatedbstr = toolatedate.format("yyyy-MM-dd' 'HH:mm:ss")

def wishdate = today 
use(TimeCategory) {
	   wishdate = wishdate + 1.day
}
def proposeddate = today
use(TimeCategory) {
	   proposeddate = proposeddate + 2.days
}
def proposeddatepast = today 
use(TimeCategory) {
	   proposeddatepast = proposeddatepast - 1.day
}
def wishdatestr = wishdate.format("yyyy-MM-dd'T'HH:mm:ss")
def propdatepast = proposeddatepast.format("yyyy-MM-dd'T'HH:mm:ss")
def propdatepaststr = proposeddatepast.format("yyyy-MM-dd' 'HH:mm:ss")
def proposeddatestr = proposeddate.format("yyyy-MM-dd'T'HH:mm:ss")
def propdatestr = proposeddate.format("yyyy-MM-dd' 'HH:mm:ss")
testRunner.testCase.setPropertyValue("Now",now)
testRunner.testCase.setPropertyValue("wishDate",wishdatestr)
testRunner.testCase.setPropertyValue("proposedDate",proposeddatestr)
testRunner.testCase.setPropertyValue("propDate",propdatestr)
testRunner.testCase.setPropertyValue("propDatePast",propdatepast)
testRunner.testCase.setPropertyValue("propDatePastStr",propdatepaststr)
testRunner.testCase.setPropertyValue("tooLateDate",toolatedatestr)
testRunner.testCase.setPropertyValue("tooLateDateDb",toolatedatedbstr)
String contractId = funcHandle.getRandomString()
testRunner.testCase.setPropertyValue("contractId",contractId)

