import org.apache.commons.lang.RandomStringUtils
import groovy.time.TimeCategory
def today = new Date()

def now = today.format("yyyy-MM-dd'T'HH:mm:ss")
def wishdate = today 
use(TimeCategory) {
	   wishdate = wishdate + 60.seconds
}
def wishdatestr = wishdate.format("yyyy-MM-dd'T23:59:59'")
def dbwishdatestr  = wishdate.format("yyyy-MM-dd' 23:59:59'")
testRunner.testCase.setPropertyValue("Now",now)
testRunner.testCase.setPropertyValue("wishDate",wishdatestr)
testRunner.testCase.setPropertyValue("dbWishDate",dbwishdatestr)
String charset = (('A'..'Z') + ('0'..'9')).join()
Integer length = 9
String contractId = RandomStringUtils.random(length, charset.toCharArray())
testRunner.testCase.setPropertyValue("contractId",contractId)

