	log.info seleniumHandle.enterDataSelectDropDown("HomePage","RequestAnswerBusinessType", "Business");	

	log.info seleniumHandle.enterDataSelectDropDownByValue("HomePage","RequestAnswerBlockingcode", "0");
} else {
	log.info seleniumHandle.enterDataSelectDropDown("HomePage","RequestAnswerBusinessType", "Consumer");

	log.info seleniumHandle.enterDataSelectDropDownByValue("HomePage","RequestAnswerBlockingcode", "0");
}
