def today = new Date() 
def now = today.format("yyyy-MM-dd'T'HH:mm:ss")
def wishdate = today 
use(TimeCategory) {
     wishdate = wishdate + 1.day
}
def proposeddate = today
use(TimeCategory) {
     proposeddate = proposeddate-1.day
}
