import nl.coin.Selenium.*;
def funcHandle = new ReadymadeFunctions();
import groovy.time.TimeCategory
def maxDays = testRunner.testCase.testSuite.getPropertyValue("maxDays").toInteger()
def today = new Date() 
def now = today.format("yyyy-MM-dd'T'HH:mm:ss")
def wishdateToday = today 
use(TimeCategory) {
     wishdateYesterday = wishdateToday-1
}
use(TimeCategory) {
     wishdateMaxDaysPlus1 = wishdateToday+ maxDays.days+1
}
def wishdateTodaystr = wishdateToday.format("yyyy-MM-dd'T'00:00:00")
def wishdateYesterdaystr = wishdateYesterday.format("yyyy-MM-dd'T'00:00:00")
def wishdateMaxDaysPlus1str = wishdateMaxDaysPlus1.format("yyyy-MM-dd'T'00:00:00")
testRunner.testCase.setPropertyValue("Now",now)
testRunner.testCase.setPropertyValue("wishdateToday",wishdateTodaystr)
testRunner.testCase.setPropertyValue("wishdateYesterday",wishdateYesterdaystr)
testRunner.testCase.setPropertyValue("wishdateMaxDaysPlus1",wishdateMaxDaysPlus1str)
String contractId = funcHandle.getRandomString()
testRunner.testCase.setPropertyValue("contractId",contractId)

