def wishdateYesterday = testRunner.testCase.getPropertyValue("proposeddateYesterday")
def onlyDateYesterday = javaHandle.getDateFromFormat(wishdateYesterday, "yyyy-MM-dd'T'HH:mm:ss")
log.info onlyDateYesterday
def monthYearYesterday = javaHandle.convertDateFormat(wishdateYesterday, "yyyy-MM-dd'T'HH:mm:ss", "MMMM yyyy")
log.info monthYearYesterday
def verification = false
if (donorDossierType == "<business>Y</business>"){
	log.info seleniumHandle.enterDataSelectDropDown("HomePage","businessType", "Business");	

	log.info seleniumHandle.enterDataSelectDropDownByValue("HomePage","blockingCode", "9");

	log.info seleniumHandle.clickObject("HomePage","CtraProposedDateIcon");

	//move calander to required month & year
	log.info seleniumHandle.moveCalenderToMonth("HomePage", "CtraProposedDateRightIcon", "HomePage", "CtraProposedDateCalHeader", monthYearYesterday)

	//verify Date is disable
	verification = seleniumHandle.verifyElementDoesNotExist("xpath","//div[@id='sendRequestAnswerProposedDateDpJs']/tbody//td/a[text()="+onlyDateYesterday+"]",true)
} else {
	log.info seleniumHandle.enterDataSelectDropDown("HomePage","businessType", "Consumer");

	log.info seleniumHandle.enterDataSelectDropDownByValue("HomePage","ConsumerCTRAblockingCode", "9");

	log.info seleniumHandle.clickObject("HomePage","CtraConsumerProposedDateIcon");

	//move calander to required month & year
	log.info seleniumHandle.moveCalenderToMonth("HomePage", "CtraConsumerProposedDateRightIcon", "HomePage", "CtraConsumerProposedDateCalHeader", monthYearYesterday)

	//verify Date is disable
	verification = seleniumHandle.verifyElementDoesNotExist("xpath","//table[@id='consumerSendRequestAnswerProposedDateDpJs']/tbody//td/a[text()="+onlyDateYesterday+"]",true)
}
log.info seleniumHandle.quitBrowser()
assert verification == true

